import {ADD_ITEM,DELETE_ITEM} from './todoTypes';

const initialState = {
    value:[{
        id:null,
        value:null
    }]
}


const todoReducer = (state = initialState, action) => {
    switch(action.type) {
        case ADD_ITEM:return {
            ...state,
            state:state.value + 1
        }
        default:return state
    }
}

export default todoReducer
